# Xử lý định dạng lại key cho RAGAS 
ragas_formatted_data = []
evalData = evalData_advancedRAG
for item in evalData_advancedRAG:
    if not isinstance(item["retrieval_context"], list) or not all(isinstance(s, str) for s in item["retrieval_context"]):
        print(f"Cảnh báo: 'retrieval_context' cho query '{item['query']}' không phải là list các strings. Bỏ qua mẫu này.")
        continue
    formatted_item = {
        "user_input": item["query"],
        "response": item["actual_output"],
        "retrieved_contexts": item["retrieval_context"],
        "reference": item.get("expected_output")
    }
    ragas_formatted_data.append(formatted_item)

# Khởi tạo LLM Evaluator cục bộ (Ollama) 
OLLAMA_MODEL_NAME = "llama3:instruct"
ollama_llm = ChatOllama(model=OLLAMA_MODEL_NAME, format="json", temperature=0.1)

evaluator_llm_ragas = LangchainLLMWrapper(ollama_llm)

# Khởi tạo Embedding Model cục bộ
EMBEDDING_MODEL_NAME = "bkai-foundation-models/vietnamese-bi-encoder"
local_embeddings = HuggingFaceEmbeddings(
        model_name=EMBEDDING_MODEL_NAME,
        # model_kwargs={'device': 'cpu'} 
        model_kwargs={'device': 'cuda'} 
    )
evaluator_embeddings_ragas = LangchainEmbeddingsWrapper(local_embeddings)

# Chuẩn bị Dataset cho RAGAS 
dataset_for_evaluation = EvaluationDataset.from_list(ragas_formatted_data) 
print(f"Đã tạo EvaluationDataset với {len(dataset_for_evaluation.samples)} mẫu.")

# Khởi tạo các Metrics với LLM và Embeddings cục bộ 
metrics_to_run_globally = [] 

faithfulness = Faithfulness(llm=evaluator_llm_ragas)
metrics_to_run_globally.append(faithfulness)

answer_relevancy = AnswerRelevancy(llm=evaluator_llm_ragas, embeddings=evaluator_embeddings_ragas)
metrics_to_run_globally.append(answer_relevancy)

context_relevance = ContextRelevance(llm=evaluator_llm_ragas)
metrics_to_run_globally.append(context_relevance)

if all(hasattr(sample, 'reference') and sample.reference is not None for sample in dataset_for_evaluation.samples):
    context_recall = ContextRecall(llm=evaluator_llm_ragas)
    metrics_to_run_globally.append(context_recall)

    context_precision = ContextPrecision(llm=evaluator_llm_ragas)
    metrics_to_run_globally.append(context_precision)
else:
    print("Bỏ qua ContextRecall và ContextPrecision vì không phải tất cả các mẫu đều có 'reference' hoặc 'reference' là None.")
print(f"\nĐánh giá với các metrics: {[m.name for m in metrics_to_run_globally]}")


# Chạy Đánh giá
def run_evaluation(current_dataset, current_metrics):
    print(f"\nThực hiện đánh giá với các metrics: {[m.name for m in current_metrics]}")
    print(f"Số lượng mẫu trong dataset: {len(current_dataset.samples)}")
    print("\nBắt đầu quá trình đánh giá với RAGAS và Ollama (có thể mất thời gian)...")
    ragas_run_config = RunConfig(max_workers=1)
    results = evaluate( 
        dataset=current_dataset,
        metrics=current_metrics,
        raise_exceptions=False,
        run_config=ragas_run_config
    )
    return results

# Chạy hàm đánh giá
if __name__ == "__main__":
    evaluation_results = run_evaluation(dataset_for_evaluation, metrics_to_run_globally)

    if evaluation_results:
        print("\nQuá trình đánh giá hoàn tất.")
        print("\n--- Kết Quả Đánh Giá (Điểm trung bình cho mỗi metric) ---")
        print(evaluation_results) 

        try:
            if isinstance(evaluation_results, dict):
                results_df = pd.DataFrame([evaluation_results]) 
            elif hasattr(evaluation_results, 'to_pandas'):
                results_df = evaluation_results.to_pandas() 
            else:
                print(f"Kiểu của evaluation_results: {type(evaluation_results)}")
                print(f"Thuộc tính của evaluation_results: {dir(evaluation_results)}")
                print("Không thể tự động chuyển evaluation_results sang Pandas DataFrame.")
                temp_df_data = []
                for i, sample_data in enumerate(ragas_formatted_data):
                    row = {
                        "user_input": sample_data["user_input"],
                        "response": sample_data["response"],
                        "reference": sample_data.get("reference")
                    }
                    temp_df_data.append(row)
                results_df = pd.DataFrame(temp_df_data)

            if not results_df.empty:
                print("\n--- Kết Quả Chi Tiết (DataFrame) ---")
                print(results_df.head()) 
                results_df.to_csv("ragas_ollama_evaluation_report.csv", index=False, encoding='utf-8-sig')
                print("\nBáo cáo chi tiết đã được lưu vào ragas_ollama_evaluation_report.csv")
            else:
                print("Không thể tạo DataFrame chi tiết từ kết quả.")

        except Exception as e:
            print(f"Lỗi khi xử lý kết quả hoặc lưu CSV: {e}")
            print("Bạn có thể xem kết quả (thường là dictionary điểm trung bình) ở trên.")
    else:
        print("Đánh giá không trả về kết quả hoặc có lỗi xảy ra trong quá trình đánh giá.")

